"""Define a method that updates your q-table."""

class QLearning(QLearning):
  def update_table(self, observation, action, reward, new_observation, terminated):
    coordinateX = 0
    coordinateY = 0
    coordinateX1 = 0
    coordinateY1 = 0


    for i in range(len(observation)):
      for j in range(len(observation[i])):
        if observation[i][j] == 1:
          coordinateX = i
          coordinateY = j
          break
    oneD_location = (coordinateX * 3) + coordinateY

    for i in range(len(new_observation)):
      for j in range(len(new_observation[i])):
        if new_observation[i][j] == 1:
          coordinateX1 = i
          coordinateY1 = j
          break
    oneD_location1 = (coordinateX1 * 3) + coordinateY1

    if terminated == False:
      new_q_value = (1 - self.alpha) * self.q_table[oneD_location][action] + self.alpha * (reward + self.gamma * np.max(self.q_table[oneD_location1]))
    else:
       new_q_value = (1 - self.alpha) * self.q_table[oneD_location][action] + self.alpha * reward

    self.q_table[oneD_location][action] = new_q_value
